#recode iso-8859-1..utf-8 $1
cat $1 | sed 's/"//g' | sed -r 's/([0-9]+),([0-9]+) - /\1;\2;/' | sed 's| / |;|g' | sed -r 's/,([0-9]+)/;\1/g' | cut -d ";" -f 1,2,3,4,5,6 | sed -r 's|([0-9]+)/([0-9]+)/([0-9]+)|20\3-\1-\2|' | awk -F ";" '{ print $6 ";" $3 ";" $4 ";" $1 "," $2 ";" $5 }' | sed -r 's/;([0-9]+),0;/;\1;/' | sort
